package week_2;

public class _3_Math {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// Example usage of Math.abs()
        int absoluteValue = Math.abs(-10);
        System.out.println("Absolute value of -10: " + absoluteValue);

        // Example usage of Math.ceil()
        double ceilValue = Math.ceil(5.25);
        System.out.println("Ceil value of 5.25: " + ceilValue);

        // Example usage of Math.floor()
        double floorValue = Math.floor(5.75);
        System.out.println("Floor value of 5.75: " + floorValue);

        // Example usage of Math.max()
        int maxValue = Math.max(10, 20);
        System.out.println("Maximum value between 10 and 20: " + maxValue);

        // Example usage of Math.min()
        int minValue = Math.min(10, 20);
        System.out.println("Minimum value between 10 and 20: " + minValue);

        // Example usage of Math.pow()
        double powerValue = Math.pow(2, 3);
        System.out.println("2 raised to the power of 3: " + powerValue);

        // Example usage of Math.sqrt()
        double sqrtValue = Math.sqrt(25);
        System.out.println("Square root of 25: " + sqrtValue);

        // Example usage of Math.exp()
        double expValue = Math.exp(1);
        System.out.println("e raised to the power of 1: " + expValue);

        // Example usage of Math.log()
        double logValue = Math.log(10);
        System.out.println("Natural logarithm of 10: " + logValue);

        // Example usage of Math.sin()
        double sinValue = Math.sin(Math.PI / 6);
        System.out.println("Sine of pi/6 radians: " + sinValue);

        // Example usage of Math.cos()
        double cosValue = Math.cos(Math.PI / 3);
        System.out.println("Cosine of pi/3 radians: " + cosValue);

        // Example usage of Math.tan()
        double tanValue = Math.tan(Math.PI / 4);
        System.out.println("Tangent of pi/4 radians: " + tanValue);


        // Example usage of Math.toDegrees()
        double degrees = Math.toDegrees(Math.PI / 2);
        System.out.println("90 degrees in radians: " + degrees);

        // Example usage of Math.toRadians()
        double radians = Math.toRadians(180);
        System.out.println("180 degrees in radians: " + radians);

        // Example usage of Math.random()
        double randomValue = Math.random();
        System.out.println("Random value between 0.0 and 1.0: " + randomValue);

	}

}
